import React from 'react';

export default function Installment({ data }) {
  const { id, value, difference, percentage, profit } = data;

  const classGoogValue = 'green-text darkeb-4';
  const classGoodPercent = 'blue-text darken-4';
  const classBadValue = 'red-text darken-4';
  const classBadPercent = 'orange-text darken-4';

  const classValue = profit ? classGoogValue : classBadValue;
  const classPercent = profit ? classGoodPercent : classBadPercent;

  return (
    <div className="col s6 m3 l2">
      <div style={styles.flexRow}>
        <span style={{ marginRight: '5px' }}>{id}</span>
        <div>
          <div>{value}</div>
          <div>{difference}</div>
          <div>{percentage}</div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  flexRow: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-start',

    border: '1px solid black',
    borderRadius: '4px',
    padding: '4px',
    margin: '4px',
  },
};
